mapper.select:
select comp_cd,anno_ymon,anno_seq,anno_titl,post_st_dd,post_ed_dd,emer_yn,anno_yn,anno_cont ,hits,post_reg_user_id,post_reg_user_nm ,atfi_id , (select count(*) from sy_atfi_info where comp_cd = #{compCd} and atfi_Id = a.atfi_Id) atfi_cnt ,to_char(to_date(reg_dd, 'yyyymmdd'), 'yyyy-mm-dd') as reg_dd ,reg_dt,reg_user_id,reg_user_ip ,update_dt,update_user_id,update_user_ip ,row_number() over(order by anno_yn desc, reg_dd desc ,anno_seq desc) rnum from bd_anno a where COMP_CD = #{compCd, jdbcType=VARCHAR}
AND (anno_titl LIKE '%'|| #{titlPostRegUserNm} ||'%' OR post_reg_user_nm LIKE '%'|| #{titlPostRegUserNm} ||'%')
AND reg_dd BETWEEN #{regDd1} AND #{regDd2} order by anno_yn desc, reg_dd desc ,anno_seq desc

mapper.update:
update bd_anno
set hits  = hits + 1
where comp_cd  = #{compCd, jdbcType=VARCHAR}
and anno_ymon = #{annoYmon, jdbcType=VARCHAR}
and anno_seq  = #{annoSeq, jdbcType=VARCHAR}

mapper.delete:
delete from bd_anno
where comp_cd = #{compCd,jdbcType=VARCHAR}
  and anno_ymon = #{annoYmon,jdbcType=VARCHAR}
  and anno_seq = #{annoSeq,jdbcType=VARCHAR}
